import React from 'react';
import { useController } from 'react-hook-form';

import {
    RowSection,
    SectionTitle,
    RedAsterisk,
    RegisterInput
} from '../../static/StyledComponents';

const FormInputSection = ({ control, name, title }) => {
    const { field: { ref, ...inputProps } } = useController({
        name: name || undefined,
        control: control,
        defaultValue: ""
    }); 

    return (
        <React.Fragment>
            <RowSection>
                <SectionTitle>{title}</SectionTitle>
                <RedAsterisk>*</RedAsterisk>
            </RowSection>
            <RegisterInput ref={ref} {...inputProps} width="20%" />
        </React.Fragment>
    );
};

export default FormInputSection;
